    print(f"{e}<escriptor>\n{e*2}<nomcomplet nom=\"{random.choice(names)}\">\n{cognoms}{e*2}</nomcomplet>\n{e*2}<idiomes>\n{idiomes}{e*2}</idiomes>\n{premis}{publicacions}{e}</escriptor>")